mapper.select:
select NB_NO, NB_TITLE, NB_CONTENT, NB_VIEWS, NB_INDATE, U_NO
from NOTICE_BOARD nb
where NB_NO = #{nbNo}

mapper.update:
update NOTICE_BOARD
set NB_VIEWS = NB_VIEWS+1
where NB_NO = #{nbNo}

update NOTICE_BOARD
set NB_TITLE = #{nbTitle},
    NB_UPDATE = default,
    NB_CONTENT = #{nbContent}
where NB_NO = #{nbNo}

mapper.select:
select QA_NO, QA_TITLE, QA_CONTENT, QA_INDATE, U_NO
from QA_BOARD qb
where QA_NO = #{qaNo}

mapper.update:
update QA_BOARD
set QA_TITLE = #{qaTitle},
QA_UPDATE = default,
QA_CONTENT = #{qaContent}
where QA_NO = #{qaNo}
